import React,{useEffect} from 'react'
import Footerr from './Footer'
import NavigationBar from './NavigationBar'
import img from '../../images/chemistry.jpg'
import NavBar from './Navbar'
import Axios  from 'axios'

export default function News() {
const [news, setNews] = React.useState();
      //User Object Post Request
  useEffect(() => {
    Axios.get("https://newsapi.org/v2/top-headlines?country=in&category=health&apiKey=9ee3c44038ca4876b022a0ce1372512e")
    .then((response) => {
        setNews(response.data);
        console.log(response.data);
    }
    )     
  },[])

  return (
<div>
<NavBar/>

{!news ? <div>NO news Found</div> : <div>{news.articles.map(data => (
    <div> <div>{data.source.name}</div> </div>
) )}</div> }

<div className='mt-3 mx-auto'>
            <div className="card mb-3" style={{maxWidth: '540px'}}>
            <div className="row g-0">
                <div className="col-md-4">
                <img src={img} className="img-fluid rounded-start" alt={img}/>
                </div>
                <div className="col-md-8">
                <div className="card-body">
                    {/* <h5 className="card-title">{n.articles[0].name}</h5> */}
                    <p className="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                    <p className="card-text"><small className="text-muted">Last updated 3 mins ago</small></p>
                </div>
                </div>
            </div>
            </div>
        </div>  

<Footerr></Footerr>
</div>
  )
}
