import React,{useEffect, useState} from 'react'
import Footerr from '../Imp/Footer'
import NavigationBar from '../Imp/NavigationBar'
import img from '../../images/chemistry.jpg'
import { Link ,useNavigate, useParams} from 'react-router-dom'
import axios from 'axios';
import { Editor } from '@tinymce/tinymce-react';
import { useRef } from 'react';
import '../../../node_modules/react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast } from 'react-toastify';

export default function EditJob() {
    const [newjobTitle, setNewJobTitle] = useState();
    const [newjobContent, setNewjobContent] = useState();
    const navigate = useNavigate();
    const [joblist, setjoblist] = useState();
    const { id } = useParams();
    console.log(id);

    useEffect(() => {
        axios.get(`http://localhost:5000/jobList`)
        .then((response) => {
            setjoblist(response.data)
            //console.log(response.data)
            console.log(joblist);
        })
        
      }, [id])

    const handleSubmit = async (e) => {
        e.preventDefault();

        try{
             await axios.put(`http://localhost:5000/editJob/${id}`,{
                newjobTitle,
                newjobContent
            } )
            .then(res => {
              toast.success('Job Updated', {
                position: "bottom-right",
                autoClose: 2500,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                });
           
            }).catch((error) => {
              if (error.response) {
                console.log(error.response);
                console.log("server responded::JobRoute");
              } else if (error.request) {
                console.log("network error ::JobRoute");
              } else {
                console.log(error);
              }
            })
          }catch(error){
            console.log(error);
          }
    }

  return (
    <div>
    <NavigationBar/>
    {id}
    <div className='container  mt-5 p-4'>
    <div className='text-secondary fs-1  '>Update Job</div>
    {/* {joblist} */}
    
    <div className=' mt-5 '>
    { joblist && joblist.filter(job => job._id === id )
    .map(j => (
      <div key={j._id} >
            <form  className="mx-auto justify-items-center align-items-center"  onSubmit={handleSubmit} id="payment-form">
            <fieldset className="FormGroup">
            <div className="row mb-3">
                  <label for="inputEmail3" className="col-sm-2 col-form-label">Job Title</label>
                  <div className="col-sm-10">
                    <input type="text" value={j.jobTitle} onChange={e => setNewJobTitle(e.target.value)} required className="form-control" id="inputEmail3" />
                  </div>
                </div>
                <div className="row mb-3">
                  <label for="inputEmail3" className="col-sm-2 col-form-label">Job Content</label>
                  <div className="col-sm-10">
                  <textarea name="textarea" value={j.jobContent}  className="form-control" required onChange={e => setNewjobContent(e.target.value)} id="inputUserId3" cols="30" rows="10"></textarea>
                  </div>
                </div>
                <div  className="row mb-3"><button type='submit' className='btn btn-primary mt-5'><Link className='text-decoration-none text-light' to='/editjobs'>Post Job</Link></button></div>
            </fieldset>
        </form>
      </div>
    ) )
    }


    </div>

    </div>
    <ToastContainer 
  position="bottom-right"
  autoClose={2000}
  hideProgressBar={false}
  newestOnTop={false}
  closeOnClick
  rtl={false}
  pauseOnFocusLoss
  draggable
  pauseOnHover
/>
    <Footerr></Footerr>
    </div>
  )
}
