import React from 'react'
import { useState, useEffect } from "react";
import {CardElement,useStripe,useElements} from "@stripe/react-stripe-js";
import axios from "axios";
import './BookTest.css';

const cardStyle = {
    style: {
      base: {
        color: "#32325d",
        fontFamily: 'Arial, sans-serif',
        fontSmoothing: "antialiased",
        fontSize: "16px",
        "::placeholder": {
          color: "#32325d"
        }
      },
      invalid: {
        fontFamily: 'Arial, sans-serif',
        color: "#fa755a",
        iconColor: "#fa755a"
      }
    }
  };

export default function PayamentForm() {
    const [success, setSuccess] = useState(false)
    const [error, setError] = useState(null);
    const [processing, setProcessing] = useState('');
    const [disabled, setDisabled] = useState(true);
    const stripe = useStripe();
    const elements = useElements();

    
  const handleSubmit = async (e) => {
    e.preventDefault();
    setProcessing(true);

    const {error, paymentMethod } = await stripe.createPaymentMethod({
        type: 'card',
        card: elements.getElement(CardElement)
    })
  

  if(!error){
      try{
          const {id} = paymentMethod
          const response = await axios.post('http://localhost:5000/payment', {
              amount:1000,
              id
          } )

          if(response.data.success){
              console.log("success payment")
              setSuccess(true);
          }
      }catch(error) {
        console.log('error' , error)
      }
  }else{
      console.log(error.message)
  }
  }

  return (

    <div>
         {!success ? 
        <form className="mx-auto justify-items-center align-items-center"  onSubmit={handleSubmit} id="payment-form">
            <fieldset className="FormGroup">
                <div className="FormRow">
                    <CardElement id="card-element" options={cardStyle}/>
                </div>
            </fieldset>
            <button id="submit" className="btn btn-primary" type="submit"><i class="bi bi-lock"></i> Pay LKR 2000</button>
        </form>
        :
       <div>
           <h2>You just bought a sweet spatula congrats this is the best decision of you're life</h2>
       </div> 
        }
    </div>
  )
}
