import React,{useEffect,useState} from 'react'
import Footerr from '../Imp/Footer'
import NavigationBar from '../Imp/NavigationBar'
import img from '../../images/chemistry.jpg'
import Axios  from 'axios'
import { Link,useParams } from 'react-router-dom'


export default function Events() {
  const [joblist, setjoblist] = useState();
  const { id } = useParams();
  useEffect(() => {
    Axios.get("http://localhost:5000/jobList")
    .then((response) => {
        setjoblist(response.data)
        console.log(response.data)
    })
    
  }, [])


  return (
<div>
<NavigationBar/>

<div className='container  mt-5 p-4'>
<div className='text-secondary fs-1  mb-5'>Job Description</div>

{!joblist ? <div>Loading...</div> : 
  joblist.filter(j => j._id === id).map(job => {
    return(
      <div className=' py-2 mt-1 rounded mb-5' key={job._id}>
      <h2 className='mb-5'>{job.jobTitle}</h2>
      <h3 className='fw-light'>  {job.jobContent} </h3>
</div>
    )
  } )
}
 <div className=''> <h2>Apply Job</h2>
<div className='bg-light p-4 fw-lighter'> If you are interested please send in your detailed CV along with names and contact details to: Head of MediTech Labs (PVT) LTD. No 258,Daniyester Mawatha,Kohuwala. or  Email: careers@meditech.gmail.com; For More Inquiries Tel: +94-112 789 589 </div></div>
  <div>
      <button className='btn btn-primary mt-5 rounded-start'><Link className='text-decoration-none text-light' to='/removejob'>Close Job</Link></button>
      <button className='btn btn-primary mt-5 rounded-end'><Link className='text-decoration-none text-light' 
      to='/Job/:id/edit'>Edit</Link></button>
  </div>
</div>
<Footerr></Footerr>
</div>
  )
}
