import React,{useEffect, useState} from 'react'
import Footerr from '../Imp/Footer'
import NavigationBar from '../Imp/NavigationBar'
import img from '../../images/chemistry.jpg'
import Axios  from 'axios'
import { Link ,useNavigate, useParams} from 'react-router-dom'
import axios from 'axios';
import { Editor } from '@tinymce/tinymce-react';
import { useRef } from 'react';
import '../../../node_modules/react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast } from 'react-toastify';

export default function EditJob() {
    const [newjobTitle, setNewJobTitle] = useState();
    const [newjobContent, setNewjobContent] = useState();
    const navigate = useNavigate();
    const [joblist, setjoblist] = useState();
    const { id } = useParams();

    useEffect(() => {
        Axios.get(`http://localhost:5000/jobList/${id}`)
        .then((response) => {
            setjoblist(response.data)
            console.log(response.data)
        })
        
      }, [])

    const handleSubmit =  (e) => {
        e.preventDefault();

        try{
             axios.post('http://localhost:5000/jobListd',{
                newjobTitle,
                newjobContent
            } )
            .then(res => {
              //console.log(jobTitle + 'Hriiiiiiiiiii');
              //console.log(blogContent)
              //alert('<div className="bg-primary">ddddddd</div>');
              //toast('Post Created')
              toast.success('Job Updated', {
                position: "bottom-right",
                autoClose: 2500,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                });
              //navigate('/careers');            
              //navigate('/blogEdit');            
            }).catch((error) => {
              if (error.response) {
                console.log(error.response);
                console.log("server responded::JobRoute");
              } else if (error.request) {
                console.log("network error ::JobRoute");
              } else {
                console.log(error);
              }
            })
          }catch(error){
            console.log(error);
          }
    }

  return (
    <div>
    <NavigationBar/>
    
    <div className='container  mt-5 p-4'>
    <div className='text-secondary fs-1  '>Update Job</div>
    {joblist}
    
    <div className=' mt-5 '>

    <form  className="mx-auto justify-items-center align-items-center"  onSubmit={handleSubmit} id="payment-form">
            <fieldset className="FormGroup">
            <div className="row mb-3">
                  <label for="inputEmail3" className="col-sm-2 col-form-label">Job Title</label>
                  <div className="col-sm-10">
                    <input type="text" value={newjobTitle} onChange={e => setNewJobTitle(e.target.value)} required className="form-control" id="inputEmail3" />
                  </div>
                </div>
                <div className="row mb-3">
                  <label for="inputEmail3" className="col-sm-2 col-form-label">Job Content</label>
                  <div className="col-sm-10">
                  <textarea name="textarea" value={newjobContent}  className="form-control" required onChange={e => setNewjobContent(e.target.value)} id="inputUserId3" cols="30" rows="10"></textarea>
                  </div>
                </div>
                <div  className="row mb-3"><button type='submit' className='btn btn-primary mt-5'><Link className='text-decoration-none text-light' to='/editjobs'>Post Job</Link></button></div>
            </fieldset>
        </form>
    </div>

    </div>
    <ToastContainer 
  position="bottom-right"
  autoClose={2000}
  hideProgressBar={false}
  newestOnTop={false}
  closeOnClick
  rtl={false}
  pauseOnFocusLoss
  draggable
  pauseOnHover
/>
    <Footerr></Footerr>
    </div>
  )
}
