import { useState,React } from "react";
import {CardElement,useStripe,useElements} from "@stripe/react-stripe-js";
import axios from "axios";
import './BookTest.css';
import DatePicker from "react-datepicker";
import setHours from "date-fns/setHours";
import setMinutes from "date-fns/setMinutes";
import getHours from 'date-fns/getHours'
import "react-datepicker/dist/react-datepicker.css";
import {Link} from 'react-router-dom';
import { DateTime } from "luxon";

const cardStyle = {
    style: {
      base: {
        color: "#32325d",
        fontFamily: 'Arial, sans-serif',
        fontSmoothing: "antialiased",
        fontSize: "16px",
        "::placeholder": {
          color: "#32325d"
        }
      },
      invalid: {
        fontFamily: 'Arial, sans-serif',
        color: "#fa755a",
        iconColor: "#fa755a"
      }
    }
  };

export default function PayamentForm() {
    const [success, setSuccess] = useState(false)
    const [processing, setProcessing] = useState('');
    const stripe = useStripe();
    const elements = useElements();
    const [firstName, setFirstName] = useState('Nick');
    const [lastName, setLastName] = useState('Jones');
    const [phoneNumber, setPhoneNumber] = useState('0771234678');
    const [email, setEmail] = useState('dwa@gmail.com');
    const [testName, setTestName] = useState();

    const [invoice, setInvoice] = useState({
      firstName,
      lastName,
      phoneNumber,
      email,
      testName,
      invoiceId
    });
    const [startDate, setStartDate] = useState();
    var num = Math.floor(Math.random() * 90000) + 10000;
    const [invoiceId, setInvoiceId] = useState(num);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setProcessing(true);

    try{
      await axios.post('http://localhost:5000/createUser',{
        firstName,
        lastName,
        email,
        phoneNumber,
        testName,
        invoiceId
      } ).then(res => {
        // const data = res.data;
        setInvoice({
          firstName: setFirstName,
          lastName: setLastName,
          phoneNumber : setPhoneNumber,
          email : setEmail,
          testName: setTestName,
          invoiceId : setInvoiceId
        })
        console.log(invoiceId + 'Hri');
        
      }).catch((error) => {
        if (error.response) {
          console.log(error.response);
          console.log("server responded::userRoute");
        } else if (error.request) {
          console.log("network error ::userRoute");
        } else {
          console.log(error);
        }
      })
    }catch(error){
      console.log(error);
    }

    const {error, paymentMethod } = await stripe.createPaymentMethod({
        type: 'card',
        card: elements.getElement(CardElement)
    })
  
    if(!error){
        try{
            const {id} = paymentMethod
            const response = await axios.post('http://localhost:5000/payment', {
                amount:1000,
                id
            } )

            if(response.data.success){
                console.log("success payment");
                setSuccess(true);
                setProcessing(true);
                
            }
