import Footerr from "./Footer";
import NavBar from "./Navbar";
import { useState, useEffect } from "react";
import Axios  from 'axios';
import Table from 'react-bootstrap/Table'
import { Link, useParams } from "react-router-dom";
import { loadStripe } from "@stripe/stripe-js";

const BookTest = () => {
    const { id } = useParams();
    const [testList, setTestList] = useState([]);
    const [changeTest, setChangeTest] = useState();
     

    useEffect(() => {
      Axios.get("http://localhost:5000/appmntt")
      .then((response) => {
          setTestList(response.data)
          console.log(response.data)
      })
      
    
    }, [])

    let stripePromise;
    const getStripe = () => {
      if(!stripePromise){
        stripePromise =  loadStripe(process.env.REACT_APP_STRIPE_KEY)
      }

      return stripePromise;
    }

    const item = {
      price: 'price_1KSwOQD1JpqiLwVaihKkW0cK',
      quantity:1
    }

    const checkoutOptions = {
      lineItems: [item],
      mode: "payment",
      successUrl: `payment/success`,
      cancelUrl: `payment/cancel`
    }

    const redirectToCheckout = async () => {
      console.log("redirectToCheckout");
    
      const stripe = await getStripe();
      const { error } = await stripe.redirectToCheckout(checkoutOptions);
      console.log("Stripe checkout error", error);
    };
      

      
    return ( 
    <div>
        <NavBar/>

        Test ID - {id}
        <div className="text-center fs-1">Book Test Appoinmnet</div>
        <div>
        {    
            testList.filter(tests => tests.test_id == id )
            .map((test) => (
                <div key={test.test_id}> {test.testName} </div>)
              )
        } 
        
        </div>

        <div className="checkout">
      <h1>Stripe Checkout</h1>
      <p className="checkout-title">Design+Code React Hooks Course</p>
      <p className="checkout-description">
        Learn how to build a website with React Hooks
      </p>
      <h1 className="checkout-price">$19</h1>
      <img
        className="checkout-product-image"
        // src={ProductImage}
        alt="Product"
      />
      <button className="checkout-button" onClick={redirectToCheckout}>
        <div className="text-container">
          <p className="text">Buy</p>
        </div>
      </button>
    </div>

<button className="btn btn-primary"><a href="https://buy.stripe.com/test_9AQ9C99xQ5lEfN6eUU" target="_blank"></a> Make oayent</button>

        <Footerr/>
    </div> );
}
 
export default BookTest;
